import React from 'react';
import {useCart} from "./CartContext";

function Cart() {
    const { cartItems } = useCart();
    let amount = 0;
    cartItems.forEach(product => amount += product.price)
    return (
        <div>
            <h1>Cart</h1>
            <ul>
                {cartItems.map((product, index) => (
                    <li key={index}> {product.name} </li>
                ))}
            </ul>
            <h2>Amount: {amount}</h2>
        </div>
    );
}

export default Cart;
