import './Products.css';
import React, {useEffect, useState} from 'react';
import axios from 'axios';

function Products() {

    const [products, setProducts] = useState([]);

    async function fetchProducts() {
        let result = [];
        await axios.get('http://localhost:8080/products')
            .then(res => {
                res.data.forEach(product => result.push(product));
                setProducts(result);
            });
    }

    useEffect(() => {
        fetchProducts();
    }, []);

    return (
        <>
            <h1>Products</h1>
            <ul>
                {

                    products.map(product => {
                        return ( <li>{product.name}, {product.price} PLN <button>Add to cart</button></li> );
                    })
                }
            </ul>
            <button id='cartButton'>Cart</button>
        </>
    );

}

export default Products;
