import './Products.css'
import React from 'react';
import axios from 'axios';


export default class Products extends React.Component {

    state = {
        products: []
    }

    componentDidMount() {
        axios.get('http://localhost:8080/products')
        .then(res => {
            products = res.data;
            this.setState({ products });
        })
    }

    mapToLi() {
        return products.map(product => {
            return (
                <li>
                    {product.name}, {product.price} PLN <button class="li_col">Add to cart</button>
                </li>
            );
        });
    }

    render() {
        return (
            <div>
                <h1>Products</h1>
                <ul>{mapToLi()}</ul>
            </div>
        );
    }
}
